import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalBugs: 0,
    openBugs: 0,
    closedBugs: 0,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/stats')
      .then(response => {
        setStats(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching statistics:', error);
        setError(error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div className="text-center mt-5">Loading...</div>;
  }

  if (error) {
    return <div className="alert alert-danger text-center mt-5">Error: {error.message}</div>;
  }

  return (
    <div>
      <h2>Dashboard</h2>
      <p>Welcome to the Bug Tracking System!</p>
      <div>
        <h3>Statistics</h3>
        <ul>
          <li>Total Bugs: {stats.totalBugs}</li>
          <li>Open Bugs: {stats.openBugs}</li>
          <li>Closed Bugs: {stats.closedBugs}</li>
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
