import React, { useEffect, useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { SocketContext } from '../context/SocketContext';
import './BugList.css';

const BugList = () => {
  const [bugs, setBugs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const socket = useContext(SocketContext);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBugs = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/bugs');
        setBugs(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching bugs:', error);
        setError(error);
        setLoading(false);
      }
    };

    fetchBugs();

    socket.on('newBug', (bug) => {
      setBugs((prevBugs) => [...prevBugs, bug]);
    });

    socket.on('updateBug', (updatedBug) => {
      setBugs((prevBugs) =>
        prevBugs.map((bug) => (bug._id === updatedBug._id ? updatedBug : bug))
      );
    });

    socket.on('deleteBug', (deletedBugId) => {
      setBugs((prevBugs) => prevBugs.filter((bug) => bug._id !== deletedBugId));
    });

    return () => {
      socket.off('newBug');
      socket.off('updateBug');
      socket.off('deleteBug');
    };
  }, [socket]);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/bugs/${id}`);
      setBugs((prevBugs) => prevBugs.filter((bug) => bug._id !== id));
    } catch (error) {
      console.error('Error deleting bug:', error);
    }
  };

  if (loading) {
    return <div className="text-center mt-5">Loading...</div>;
  }

  if (error) {
    return <div className="alert alert-danger text-center mt-5">Error: {error.message}</div>;
  }

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Bug List</h1>
      <ul className="list-group">
        {bugs.map((bug) => (
          <li key={bug._id} className="list-group-item">
            <h2>{bug.title}</h2>
            <p>{bug.description}</p>
            <p>Status: {bug.status}</p>
            <p>Created At: {new Date(bug.created_at).toLocaleString()}</p>
            <p>Updated At: {new Date(bug.updated_at).toLocaleString()}</p>
            <button
              className="btn btn-primary m-2"
              onClick={() => navigate(`/bug/${bug._id}`)}
            >
              View
            </button>
            <button
              className="btn btn-secondary m-2"
              onClick={() => navigate(`/edit/${bug._id}`)}
            >
              Edit
            </button>
            <button
              className="btn btn-danger m-2"
              onClick={() => handleDelete(bug._id)}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BugList;
