import React, { useEffect, useState } from 'react';
import './BugList.css';

const BugList = () => {
  const [bugs, setBugs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/api/bugs')
      .then(response => response.json())
      .then(data => {
        setBugs(data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching bugs:', error);
        setError(error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div className="text-center mt-5">Loading...</div>;
  }

  if (error) {
    return <div className="alert alert-danger text-center mt-5">Error: {error.message}</div>;
  }

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Bug List</h1>
      <ul className="list-group">
        {bugs.map(bug => (
          <li key={bug._id} className="list-group-item">
            <h2>{bug.title}</h2>
            <p>{bug.description}</p>
            <p>Status: {bug.status}</p>
            <p>Created At: {new Date(bug.created_at).toLocaleString()}</p>
            <p>Updated At: {new Date(bug.updated_at).toLocaleString()}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BugList;
